import React, { useEffect } from 'react';
import { Routes, Route, redirect } from 'react-router-dom';
import Header from './components/Header';
import Auth from './components/Auth';
import Blogs from './components/Blogs';
import AddBlog from './components/AddBlog';
import UserBlogs from './components/UserBlogs';
import BlogDetail from './components/BlogDetail';
import { useSelector, useDispatch } from 'react-redux';
import { authActions } from "./store";

function App() {
  const dispath = useDispatch();

  const isLoggedIn = useSelector(state => state.isLoggedIn);
  console.log(isLoggedIn);
  useEffect(() => { 
    if(localStorage.getItem("userId")) {
       dispath(authActions.login());
    }

  }, [dispath])

  return (
    <React.Fragment>
      <header>
        <Header />
      </header>
      <main>
        <Routes>
          {!isLoggedIn ? (<>
            <Route path="/auth" loader={async () => {return redirect('/auth/login')}} element={<></>} />
            <Route path="/auth/login" element={<Auth isSignUp={false} />} />
            <Route path="/auth/signup" element={<Auth isSignUp={true} />} />
          </>) : (
            <>
              <Route path="/auth" loader={() => redirect('/blogs')} element={<></>} />
              <Route path="/blogs" element={<Blogs />} />
              <Route path="/blogs/add" element={<AddBlog />} />
              <Route path="/myBlogs" element={<UserBlogs />} />
              <Route path="/myBlogs/:id" element={<BlogDetail />} />
            </>
          )}
        </Routes>
      </main>
    </React.Fragment>
  );
}

export default App;
